import { Formik } from "formik";
import { useContext } from "react";
import BookContext from "../../context/BookContext";
import * as Yup from "yup";
import { IBook } from "../../interfaces/IBook";

const BookSchema = Yup.object().shape({
  text: Yup.string().min(3, "Goal is too short").required("Goal is required"),
});

const BookForm = () => {
  const { createBook } = useContext(BookContext);

  const addBook = async (values: IBook) => {
    try {
      const response = createBook(values);

      console.log(response);
    } catch (error) {
      throw error;
    }
  };

  return (
    <Formik
      initialValues={{
        title: "",
        author_name: "",
      }}
      onSubmit={addBook}
      validationSchema={BookSchema}
    >
      {({ handleSubmit }) => <form></form>}
      {({ handleSubmit }) => (
        <form className={classNames("px-6", className)} onSubmit={handleSubmit}>
          <button onClick={addBook} type="button">
            Add
          </button>
        </form>
      )}
    </Formik>
  );
};

export default BookForm;
