import classNames from "classnames";
import { FieldHookConfig, useField } from "formik";

type Props = {
  name: string;
  label?: string;
  placeholder?: string;
  disabled?: boolean;
  type?: string;
};

const Input = function ({
  name,
  label = "",
  placeholder = "",
  disabled,
  type = "",
}: Props) {
  return (
    <FieldFormik name={name}>
      {(formikMeta) => {
        const { meta } = formikMeta;
        const showError =
          ((dirty || meta?.touched) && meta?.error) || overrideError;

        return (
          <FormGroup className={classNames("w-full", className)}>
            {!!label && type !== "switch" && (
              <Label className="block">
                {label} {required && "*"}
              </Label>
            )}
            {getFormInput(
              { ...formikMeta, value: formikMeta?.value || "" },
              !!showError
            )}
            {showError && (
              <FormFeedback className="!block">
                {overrideError || meta?.error || "error"}
              </FormFeedback>
            )}
            {helperText && <FormText>{helperText}</FormText>}
          </FormGroup>
        );
      }}
    </FieldFormik>
  );
};

export default Input;
