import classNames from "classnames";
import { Field, FieldHookConfig, useField } from "formik";

type Props = {
  name: string;
  className?: string;
  label?: string;
  placeholder?: string;
  disabled?: boolean;
  type?: string;
};

const Input = function ({
  name,
  className = "",
  label = "",
  placeholder = "",
  disabled,
  type = "",
}: Props) {
  return (
    <Field name={name}>
      {(formikMeta) => {
        const { meta } = formikMeta;
        const showError = meta?.touched && meta?.error;

        return (
          <div className={classNames("w-full", className)}>
            {!!label && type !== "switch" && (
              <Label className="block">
                {label} {required && "*"}
              </Label>
            )}
            <Input
              data-testid={dataTestId}
              type={type}
              name={name}
              invalid={showError}
              disabled={disabled}
              {...formikMeta}
              placeholder={placeholder}
            />
            {showError && <p>{meta?.error}</p>}
          </div>
        );
      }}
    </Field>
  );
};

export default Input;
