import classNames from "classnames";
import { FieldHookConfig, useField } from "formik";

type Props = {
  name: string;
  label?: string;
  placeholder?: string;
  fieldProps: FieldHookConfig<string>;
  disabled?: boolean;
  type?: string;
};

const Input = function ({
  label = "",
  placeholder = "",
  fieldProps,
  disabled,
  type = "",
}: Props) {
  const [field, meta] = useField(fieldProps);

  return (
    <div className="flex flex-col">
      {label && <p className="mb-2">{label}</p>}
      <input
        {...field}
        name={name}
        placeholder={placeholder}
        type={type}
        disabled={disabled}
        className={classNames(
          "transition border border-light-300 hover:border-gray-500 focus:border-gray-500 outline-none w-full rounded-lg text-lg px-4 py-4",
          {
            "bg-light-300 text-light-600 disabled:hover:border-light-300":
              disabled,
          }
        )}
      />
      {meta.touched && meta.error && (
        <p className="text-red-400 text-sm mt-2">{meta.error}</p>
      )}
    </div>
  );
};

export default Input;
